package examples;

public class ExampleDoWhileLoop {
	
	/**
	 * @param args
	 * @author Nina | Tagfun | http://www.jmdprojects.net/blog-tagfun
	 */

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
		
		do {
			System.out.println("x = " + x);
			x--;
			
		} while (x >= 0);

	}
}
