package examples;

public class WhileLoop {
	
	/**
	 * @param args
	 * @author Nina | Tagfun | http://www.jmdprojects.net/blog-tagfun
	 */

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = -10;
		
		while(x <= 0) {
			System.out.println("x = " + x);
			x++;
			// This is the same thing as x++ --> x = x + 1;
		}

	}

}
